import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useLocation } from "react-router-dom";
import { useContactsCrud } from "../context/ContactsCrudContext";
import * as yup from "yup";

const schema = yup.object().shape({
  name: yup.string().required(),
  email: yup.string().email().required(),
  age: yup.string().required(),
  sex: yup.string().required(),
  city: yup.string().required(),
});

const cities = [
  "Beirut",
  "Tripoli",
  "Sidon",
  "Zahle",
  "Tyre",
  "Danniyeh",
  "Jounieh",
  "Baalbek",
  "Akkar",
  "Aley",
  "Nabatieh",
  "Byblos",
  "Batroun",
  "Zgharta",
];

const EditContact = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { id, name, email, age, sex, city } = location.state.contact;

  const [newName, setNewName] = useState(name);
  const [newEmail, setNewEmail] = useState(email);
  const [newAge, setNewAge] = useState(age);
  const [newSex, setNewSex] = useState(sex);
  const [newCity, setNewCity] = useState(city);
  const { updateContactHandler } = useContactsCrud();

  const update = async (e) => {
    e.preventDefault();
    try {
      await schema.validate({ newName, newEmail, newAge, newSex, newCity });

      updateContactHandler({
        id,
        name: newName,
        email: newEmail,
        age: newAge,
        sex: newSex,
        city: newCity,
      });
      setNewName("");
      setNewEmail("");
      setNewAge("");
      setNewSex("");
      setNewCity("");
      navigate("/");
    } catch (err) {
      if (err.message.includes("email")) {
        alert("Email must be a valid email!");
      } else if (err.message.includes("required")) {
        alert("All fields are mandatory!");
      }
    }
  };
  return (
    <div
      className="ui main"
      style={{
        paddingTop: "50px",
      }}
    >
      <h2>Edit Contact</h2>
      <form className="ui form" onSubmit={update}>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Name</label>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Email</label>
          <input
            type="text"
            name="email"
            placeholder="Email"
            value={newEmail}
            onChange={(e) => setNewEmail(e.target.value)}
          ></input>
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Age</label>
          <input
            type="number"
            name="age"
            placeholder="Age"
            value={newAge}
            onChange={(e) => setNewAge(e.target.value)}
          ></input>
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Sex</label>
          <div>
            <input
              type="radio"
              id="male"
              name="sex"
              value="Male"
              checked={newSex === "Male"}
              onChange={(e) => setNewSex(e.target.value)}
            />
            <label htmlFor="male">Male</label>
          </div>
          <div>
            <input
              type="radio"
              id="female"
              name="sex"
              value="Female"
              checked={newSex === "Female"}
              onChange={(e) => setNewSex(e.target.value)}
            />
            <label htmlFor="female">Female</label>
          </div>
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>City</label>
          <select
            name="city"
            value={newCity}
            onChange={(e) => setNewCity(e.target.value)}
          >
            <option value="">Select City</option>
            {cities.map((city, index) => (
              <option key={index} value={city}>
                {city}
              </option>
            ))}
          </select>
        </div>
        <button className="ui button blue">Update</button>
      </form>
    </div>
  );
};

export default EditContact;
