import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const cities = [
  // ...
];

const EditContact = (props) => {
  const navigate = useNavigate();
  const location = useLocation();
  const { id, name, email, age, sex, city } = location.state.contact;

  const [contact, setContact] = useState({ id, name, email, age, sex, city });

  const update = (e) => {
    e.preventDefault();
    if (
      contact.name === "" ||
      contact.email === "" ||
      contact.age === "" ||
      contact.sex === "" ||
      contact.city === ""
    ) {
      alert("All the fields are mandatory!");
      return;
    }
    props.updateContactHandler(contact);
    setContact({ name: "", email: "", age: "", sex: "", city: "" });
    navigate("/");
  };

  const handleChange = (e) => {
    setContact({ ...contact, [e.target.name]: e.target.value });
  };

  return (
    <div
      className="ui main"
      style={{
        paddingTop: "50px",
      }}
    >
      <h2>Edit Contact</h2>
      <form className="ui form" onSubmit={this.update}>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Name</label>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={this.state.name}
            onChange={(e) => this.setState({ name: e.target.value })}
          />
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Email</label>
          <input
            type="text"
            name="email"
            placeholder="Email"
            value={this.state.email}
            onChange={(e) => this.setState({ email: e.target.value })}
          ></input>
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Age</label>
          <input
            type="number"
            name="age"
            placeholder="Age"
            value={this.state.age}
            onChange={(e) => this.setState({ age: e.target.value })}
          ></input>
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Sex</label>
          <div>
            <input
              type="radio"
              id="male"
              name="sex"
              value="Male"
              checked={this.state.sex === "Male"}
              onChange={(e) => this.setState({ sex: e.target.value })}
            />
            <label htmlFor="male">Male</label>
          </div>
          <div>
            <input
              type="radio"
              id="female"
              name="sex"
              value="Female"
              checked={this.state.sex === "Female"}
              onChange={(e) => this.setState({ sex: e.target.value })}
            />
            <label htmlFor="female">Female</label>
          </div>
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>City</label>
          <select
            name="city"
            value={this.state.city}
            onChange={(e) => this.setState({ city: e.target.value })}
          >
            <option value="">Select City</option>
            {cities.map((city, index) => (
              <option key={index} value={city}>
                {city}
              </option>
            ))}
          </select>
        </div>
        <button className="ui button blue">Update</button>
      </form>
    </div>
  );
};

export default EditContact;
