import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api/contacts";

const cities = [
  "Beirut",
  "Tripoli",
  "Sidon",
  "Zahle",
  "Tyre",
  "Danniyeh",
  "Jounieh",
  "Baalbek",
  "Akkar",
  "Aley",
  "Nabatieh",
  "Byblos",
  "Batroun",
  "Zgharta",
];

const EditContact = (props) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [contact, setContact] = useState({
    id: "",
    name: "",
    email: "",
    age: "",
    sex: "",
    city: "",
  });

  useEffect(() => {
    const retrieveContact = async () => {
      const response = await api.get(`/contacts/${id}`);
      setContact(response.data);
    };
    retrieveContact();
  }, [id]);

  const update = async (e) => {
    e.preventDefault();
    if (
      contact.name === "" ||
      contact.email === "" ||
      contact.age === "" ||
      contact.sex === "" ||
      contact.city === ""
    ) {
      alert("All the fields are mandatory!");
      return;
    }
    await props.updateContactHandler(contact);
    navigate("/");
  };

  const handleChange = (e) => {
    setContact({ ...contact, [e.target.name]: e.target.value });
  };

  return (
    <div
      className="ui main"
      style={{
        paddingTop: "50px",
      }}
    >
      <h2>Edit Contact</h2>
      <form className="ui form" onSubmit={this.add}>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Name</label>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={this.state.name}
            onChange={(e) => this.setState({ name: e.target.value })}
          />
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Email</label>
          <input
            type="text"
            name="email"
            placeholder="Email"
            value={this.state.email}
            onChange={(e) => this.setState({ email: e.target.value })}
          ></input>
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Age</label>
          <input
            type="number"
            name="age"
            placeholder="Age"
            value={this.state.age}
            onChange={(e) => this.setState({ age: e.target.value })}
          ></input>
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Sex</label>
          <div>
            <input
              type="radio"
              id="male"
              name="sex"
              value="Male"
              checked={this.state.sex === "Male"}
              onChange={(e) => this.setState({ sex: e.target.value })}
            />
            <label htmlFor="male">Male</label>
          </div>
          <div>
            <input
              type="radio"
              id="female"
              name="sex"
              value="Female"
              checked={this.state.sex === "Female"}
              onChange={(e) => this.setState({ sex: e.target.value })}
            />
            <label htmlFor="female">Female</label>
          </div>
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>City</label>
          <select
            name="city"
            value={this.state.city}
            onChange={(e) => this.setState({ city: e.target.value })}
          >
            <option value="">Select City</option>
            {cities.map((city, index) => (
              <option key={index} value={city}>
                {city}
              </option>
            ))}
          </select>
        </div>
        <button className="ui button blue">Add</button>
      </form>
    </div>
  );
};

export default EditContact;
