import React from "react";
import { useNavigate } from "react-router-dom";
import { useLocation } from "react-router-dom";

const cities = [
  "Beirut",
  "Tripoli",
  "Sidon",
  "Zahle",
  "Tyre",
  "Danniyeh",
  "Jounieh",
  "Baalbek",
  "Akkar",
  "Aley",
  "Nabatieh",
  "Byblos",
  "Batroun",
  "Zgharta",
];

const EditContactWithRouter = (props) => {
  const navigate = useNavigate();
  console.log(props.location.state.contact);
  return <EditContact {...props} navigate={navigate} />;
};

const EditContact = () => {
  const location = useLocation();
  const { id, name, email, age, sex, city } = location.state.contact;

  const [newName, setNewName] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newAge, setNewAge] = useState("");
  const [newSex, setNewSex] = useState("");
  const [newCity, setNewCity] = useState("");

  update = (e) => {
    e.preventDefault();
    if (
      this.state.name === "" ||
      this.state.email === "" ||
      this.state.age === "" ||
      this.state.sex === "" ||
      this.state.city === ""
    ) {
      alert("All the fields are mandatory!");
      return;
    }
    this.props.updateContactHandler(this.state);
    this.setState({ name: "", email: "", age: "", sex: "", city: "" });
    this.props.navigate("/");
  };
  return (
    <div
      className="ui main"
      style={{
        paddingTop: "50px",
      }}
    >
      <h2>Edit Contact</h2>
      <form className="ui form" onSubmit={this.update}>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Name</label>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={this.state.name}
            onChange={(e) => this.setState({ name: e.target.value })}
          />
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Email</label>
          <input
            type="text"
            name="email"
            placeholder="Email"
            value={this.state.email}
            onChange={(e) => this.setState({ email: e.target.value })}
          ></input>
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Age</label>
          <input
            type="number"
            name="age"
            placeholder="Age"
            value={this.state.age}
            onChange={(e) => this.setState({ age: e.target.value })}
          ></input>
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>Sex</label>
          <div>
            <input
              type="radio"
              id="male"
              name="sex"
              value="Male"
              checked={this.state.sex === "Male"}
              onChange={(e) => this.setState({ sex: e.target.value })}
            />
            <label htmlFor="male">Male</label>
          </div>
          <div>
            <input
              type="radio"
              id="female"
              name="sex"
              value="Female"
              checked={this.state.sex === "Female"}
              onChange={(e) => this.setState({ sex: e.target.value })}
            />
            <label htmlFor="female">Female</label>
          </div>
        </div>
        <div className="field">
          <label style={{ fontSize: "1.2em" }}>City</label>
          <select
            name="city"
            value={this.state.city}
            onChange={(e) => this.setState({ city: e.target.value })}
          >
            <option value="">Select City</option>
            {cities.map((city, index) => (
              <option key={index} value={city}>
                {city}
              </option>
            ))}
          </select>
        </div>
        <button className="ui button blue">Update</button>
      </form>
    </div>
  );
};

export default EditContactWithRouter;
