import React from "react";
import user from "../images/user.png";
import { Link } from "react-router-dom";
import { useContactsCrud } from "../context/ContactsCrudContext";

const ContactCard = (props) => {
  const { removeContactHandler } = useContactsCrud;
  const { id, name, email, age, sex, city } = props.contact;
  console.log(props.contact);
  return (
    <div
      className="item"
      style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "10px",
        border: "1px solid #ddd",
        borderRadius: "5px",
        marginBottom: "10px",
      }}
    >
      <img className="ui avatar image" src={user} alt="user" />
      <div
        className="content"
        style={{
          display: "flex",
          flex: "1",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <div style={{ width: "10%", textAlign: "center" }}>{name}</div>
        <div style={{ width: "50%", textAlign: "center" }}>{email}</div>
        <div style={{ width: "13.33%", textAlign: "center" }}>{age}</div>
        <div style={{ width: "13.33%", textAlign: "center" }}>{sex}</div>
        <div
          style={{ width: "13.33%", textAlign: "center", marginRight: "15%" }}
        >
          {city}
        </div>
      </div>

      <Link to={{ pathname: "/edit/", state: { contact: props.contact } }}>
        <i
          className="edit alternate outline icon"
          style={{ color: "green" }}
        ></i>
      </Link>

      <i
        className="trash alternate outline icon right floated"
        style={{ color: "red", marginLeft: "10px", cursor: "pointer" }}
        onClick={() => props.clickHandler(id)}
      ></i>
    </div>
  );
};

export default ContactCard;
